SELECT MIN(role_type.role), MIN(aka_title.movie_id), MIN(movie_info_idx.movie_id), MIN(movie_info.note), MIN(cast_info.movie_id), MIN(company_type.kind), MIN(movie_info.movie_id) FROM movie_info, cast_info, company_type, aka_title, movie_info_idx, role_type WHERE role_type.role IS NOT NULL AND movie_info.note != '(featured on Unforgiven DVD)' AND company_type.kind IN ('distributors', 'production companies', 'special effects companies', 'miscellaneous companies') AND movie_info.movie_id = cast_info.movie_id AND movie_info.movie_id = movie_info_idx.movie_id AND movie_info.movie_id = aka_title.movie_id;