SELECT MIN(title.kind_id), MIN(kind_type.id), MIN(link_type.link), MIN(company_name.id), MIN(company_name.country_code), MIN(movie_companies.company_id), MIN(kind_type.kind) FROM link_type, movie_companies, title, kind_type, company_name WHERE company_name.country_code = '[bm]' AND kind_type.kind IN ('movie', 'video movie', 'video game', 'episode', 'tv movie', 'tv series', 'tv mini series') AND link_type.link IN ('features', 'alternate language version of', 'version of', 'spin off from', 'follows', 'references', 'similar to', 'remade as', 'spoofed in', 'unknown link', 'featured in', 'edited into', 'remake of', 'edited from', 'spoofs', 'spin off', 'followed by', 'referenced in') AND kind_type.id = title.kind_id AND company_name.id = movie_companies.company_id;