SELECT MIN(cast_info.note), MIN(title.kind_id), MIN(movie_companies.note), MIN(company_name.name), MIN(aka_title.movie_id), MIN(movie_companies.movie_id), MIN(title.id), MIN(movie_keyword.movie_id), MIN(keyword.keyword), MIN(title.production_year), MIN(complete_cast.movie_id), MIN(company_type.id), MIN(movie_companies.company_type_id), MIN(aka_name.name), MIN(kind_type.id) FROM movie_companies, title, aka_name, cast_info, company_type, kind_type, aka_title, movie_keyword, complete_cast, keyword, company_name WHERE movie_companies.note != '(as Challenge Productions)' AND title.production_year = 1986 AND aka_name.name IN ('Dilip, K.', 'Gaggiano, Roberto', 'Strait, George Harvey', 'Miller, Professor Merton', 'Russell, Silky', 'Medlin, Matt', 'Callaghan, Leonard James', 'Jimmy', 'Rambo Sambo', 'Bill', 'Sam', 'Jay', 'Alex', 'Bob', 'Joe', 'Saldierna, Rechina', 'Mike', 'credits, Hironori Kanno in English', 'Tony', 'Chris') AND company_name.name = 'Nickelodeon' AND cast_info.note IS NOT NULL AND keyword.keyword IN ('butchering-cattle', 'haitian-voodoo', 'barrier-cliff', 'covered-in-mud', 'congressional-gold-medal', 'reference-to-hugh-o''neill-earl-of-tyrone', 'unhappiness', 'socialismus', 'lake-havasu', 'electronic-bank-robbery', 'communications-director', 'bunny-love', 'baseball-game', 'designer-handbag', 'suicide-by-falling', 'munitions-train', 'actuary', 'coal-town', 'naval-escort', 'religious-sect') AND kind_type.id = title.kind_id AND movie_companies.movie_id = complete_cast.movie_id AND title.id = movie_keyword.movie_id AND title.id = aka_title.movie_id AND company_type.id = movie_companies.company_type_id;