SELECT MIN(person_info.person_id), MIN(cast_info.note), MIN(company_name.country_code), MIN(role_type.role), MIN(cast_info.person_id), MIN(keyword.keyword) FROM company_name, person_info, cast_info, role_type, keyword WHERE keyword.keyword IS NOT NULL AND cast_info.note IN ('(as Rosa Frausto)', '(producer)', '(script supervisor) (as Schno Mozingo)', '(as Joe Pazos)', '(writer)', '(as Gustave Von Seyffertitz)', '(production financing) (as Christl Bucina)', '(co-executive producer) (as Tron Hauge)', '(production assistant)', '(creator)', '(co-executive producer) (as Lyn Greene)', '(accountant: TFC Trickompany)', '(production coordinator)', '(executive producer)', '(as Bulgari)', '(voice)', '(producer) (as Koh Shibusawa)', '(associate producer)', '(uncredited)', '(written by)') AND role_type.role = 'director' AND company_name.country_code = '[gl]' AND cast_info.person_id = person_info.person_id;