SELECT MIN(aka_title.movie_id), MIN(title.title), MIN(movie_companies.movie_id), MIN(title.id), MIN(movie_link.linked_movie_id), MIN(movie_link.movie_id), MIN(complete_cast.movie_id), MIN(movie_info.movie_id) FROM movie_companies, movie_info, title, aka_title, movie_link, complete_cast WHERE title.title IS NOT NULL AND title.id = complete_cast.movie_id AND movie_companies.movie_id = title.id AND title.id = movie_link.movie_id AND title.id = aka_title.movie_id AND title.id = movie_link.linked_movie_id AND movie_info.movie_id = title.id;