SELECT MIN(info_type.id), MIN(aka_title.movie_id), MIN(movie_info.info_type_id), MIN(movie_keyword.movie_id), MIN(movie_info.note), MIN(company_name.country_code), MIN(movie_info.movie_id) FROM movie_info, aka_title, info_type, movie_keyword, company_name WHERE movie_info.note = '(Finland: 2002)' AND company_name.country_code != '[ee]' AND info_type.id = movie_info.info_type_id AND movie_keyword.movie_id = movie_info.movie_id AND movie_info.movie_id = aka_title.movie_id;