SELECT MIN(role_type.role), MIN(person_info.note), MIN(movie_info_idx.info), MIN(movie_companies.note), MIN(company_name.name), MIN(title.title), MIN(aka_name.name), MIN(link_type.link), MIN(movie_info.note), MIN(title.production_year), MIN(keyword.keyword), MIN(name.name_pcode_cf), MIN(char_name.name), MIN(company_type.kind) FROM link_type, title, movie_info, movie_companies, aka_name, company_type, name, movie_info_idx, role_type, person_info, keyword, char_name, company_name WHERE name.name_pcode_cf IS NULL AND company_name.name != 'Westofi-Tonfilm-Productions' AND keyword.keyword = 'haitian-voodoo' AND aka_name.name IN ('Saldierna, Rechina', 'Dilip, K.', 'credits, Hironori Kanno in English', 'Chris', 'Strait, George Harvey', 'Jay', 'Miller, Professor Merton', 'Medlin, Matt', 'Jimmy', 'Sam', 'Bill', 'Mike', 'Tony', 'Gaggiano, Roberto', 'Joe', 'Rambo Sambo', 'Alex', 'Bob', 'Russell, Silky', 'Callaghan, Leonard James') AND movie_companies.note = '(2007) (USA) (DVD)' AND movie_info_idx.info = '0....3.3.1' AND company_type.kind = 'special effects companies' AND title.production_year > 2002 AND movie_info.note IS NOT NULL AND role_type.role != 'actor' AND char_name.name != 'Archibald' AND link_type.link != 'similar to' AND title.title = '(#1.1)' AND person_info.note IS NOT NULL;