SELECT MIN(movie_info_idx.info), MIN(role_type.role), MIN(movie_companies.note), MIN(comp_cast_type.kind), MIN(name.gender), MIN(company_name.name), MIN(title.title), MIN(title.episode_nr), MIN(movie_companies.movie_id), MIN(title.id), MIN(movie_keyword.movie_id), MIN(link_type.link), MIN(company_name.country_code), MIN(title.production_year), MIN(keyword.keyword), MIN(movie_info_idx.movie_id), MIN(aka_name.name), MIN(company_type.kind) FROM movie_companies, link_type, title, aka_name, name, company_type, movie_info_idx, movie_keyword, role_type, keyword, company_name, comp_cast_type WHERE keyword.keyword = 'electronic-bank-robbery' AND title.production_year = 2008 AND title.title = 'Eine Allerweltsgeschichte' AND company_name.name IN ('Sony Pictures Releasing', 'Overnight Productions', 'Westofi-Tonfilm-Productions', 'DOD Entertainment', 'Trends Media Group', 'Nickelodeon', 'Walt Disney Studios Motion Pictures', 'Universal Pictures International (UPI)', 'Flying Rhino Productions', 'Buena Vista International', 'Bae Yong-Kyun Productions', 'H.I.S.', '20th Century Fox', 'Atmospheric Pictures', 'No Fear Here Productions', 'Reel DVD', 'AXN', 'Warner Bros.', 'Sony Entertainment Television', 'United International Pictures (UIP)') AND comp_cast_type.kind IS NULL AND name.gender IN ('', 'm', 'f') AND company_name.country_code IS NOT NULL AND role_type.role = 'miscellaneous crew' AND company_type.kind = 'distributors' AND movie_companies.note = '(supported by) (as Nordvisionsfonden)' AND title.episode_nr <= 22988 AND link_type.link != 'unknown link' AND aka_name.name = 'Dilip, K.' AND movie_info_idx.info IS NULL AND movie_info_idx.movie_id = movie_companies.movie_id AND title.id = movie_keyword.movie_id;