SELECT MIN(movie_companies.note), MIN(movie_info.note), MIN(info_type.info), MIN(title.production_year), MIN(complete_cast.movie_id), MIN(movie_info.movie_id) FROM movie_companies, movie_info, title, info_type, complete_cast WHERE movie_companies.note = '(2006) (USA) (DVD)' AND movie_info.note = '(certificate #30342)' AND title.production_year > 1974 AND info_type.info != 'LD contrast' AND movie_info.movie_id = complete_cast.movie_id;