SELECT MIN(role_type.role), MIN(movie_companies.note), MIN(link_type.link), MIN(aka_name.name), MIN(kind_type.kind) FROM movie_companies, link_type, aka_name, kind_type, role_type WHERE link_type.link IS NOT NULL AND kind_type.kind IS NOT NULL AND role_type.role = 'editor' AND movie_companies.note IN ('(2006) (Slovenia) (DVD)', '(as Challenge Productions)', '(co-production)', '(????) (USA) (theatrical) (dubbed) (as "Sidewalk Doctor")', '(2005) (USA) (DVD)', '(2008) (Canada) (theatrical) (Fantasia International Film Festival)', '(2006) (USA) (DVD)', '(2007) (USA) (DVD)', '(1996) (USA) (VHS) (on "Bucky''s Dragon Art Theatre Triple XXX Double Feature Vol. 1")', '(2004) (USA) (DVD)', '(1975) (Canada) (theatrical) (re-release)', '(2009) (USA) (DVD)', '(in association with)', '(1986) (USA) (VHS) (as "Beyond the Living Dead")', '(presents)', '(2008) (USA) (DVD)', '(1997) (Italy) (video)', '(participation)', '(supported by) (as Nordvisionsfonden)', '(1997) (Japan) (all media) (Pippin Atmark)') AND aka_name.name = 'Jay';