SELECT MIN(movie_info_idx.info), MIN(info_type.id), MIN(movie_companies.movie_id), MIN(title.id), MIN(movie_info_idx.movie_id), MIN(movie_keyword.movie_id), MIN(complete_cast.movie_id), MIN(movie_info_idx.info_type_id), MIN(kind_type.kind) FROM movie_companies, title, kind_type, info_type, movie_keyword, movie_info_idx, complete_cast WHERE movie_info_idx.info = '3...1.3.1.' AND kind_type.kind != 'video movie' AND info_type.id = movie_info_idx.info_type_id AND movie_info_idx.movie_id = title.id AND movie_info_idx.movie_id = complete_cast.movie_id AND movie_info_idx.movie_id = movie_companies.movie_id AND movie_info_idx.movie_id = movie_keyword.movie_id;