SELECT MIN(role_type.role), MIN(complete_cast.subject_id), MIN(comp_cast_type.kind), MIN(company_name.name), MIN(person_info.person_id), MIN(aka_name.person_id), MIN(name.id), MIN(movie_companies.company_type_id), MIN(complete_cast.status_id), MIN(name.name_pcode_cf), MIN(comp_cast_type.id), MIN(company_type.id), MIN(company_name.id), MIN(company_type.kind), MIN(movie_companies.company_id) FROM name, movie_companies, aka_name, company_type, role_type, complete_cast, person_info, company_name, comp_cast_type WHERE company_name.name = '20th Century Fox' AND comp_cast_type.kind = 'crew' AND name.name_pcode_cf IS NULL AND company_type.kind IN ('miscellaneous companies', 'distributors', 'special effects companies', 'production companies') AND role_type.role = 'cinematographer' AND name.id = aka_name.person_id AND comp_cast_type.id = complete_cast.status_id AND company_name.id = movie_companies.company_id AND name.id = person_info.person_id AND company_type.id = movie_companies.company_type_id AND comp_cast_type.id = complete_cast.subject_id;