SELECT MIN(cast_info.person_role_id), MIN(name.gender), MIN(title.title), MIN(title.episode_nr), MIN(name.name), MIN(info_type.info), MIN(char_name.id), MIN(char_name.name) FROM name, title, cast_info, info_type, char_name WHERE name.name != 'Williams, Michael' AND name.gender != 'm' AND char_name.name != 'Himself - Field Reporter' AND title.episode_nr < 70460 AND info_type.info = 'runtimes' AND title.title != '(#1.3)' AND char_name.id = cast_info.person_role_id;