SELECT MIN(company_type.id), MIN(company_type.kind), MIN(info_type.id), MIN(person_info.person_id), MIN(person_info.note), MIN(person_info.info_type_id), MIN(movie_companies.company_type_id), MIN(kind_type.kind), MIN(aka_name.person_id) FROM kind_type, movie_companies, company_type, aka_name, person_info, info_type WHERE company_type.kind != 'miscellaneous companies' AND kind_type.kind IS NULL AND person_info.note = 'L.Knight' AND person_info.person_id = aka_name.person_id AND info_type.id = person_info.info_type_id AND company_type.id = movie_companies.company_type_id;