SELECT MIN(cast_info.movie_id), MIN(company_type.kind), MIN(cast_info.note), MIN(movie_info_idx.movie_id), MIN(info_type.info), MIN(char_name.name) FROM company_type, cast_info, char_name, movie_info_idx, info_type WHERE company_type.kind != 'distributors' AND cast_info.note = '(as Bulgari)' AND info_type.info != 'pictorial' AND char_name.name IS NOT NULL AND cast_info.movie_id = movie_info_idx.movie_id;