SELECT MIN(role_type.role), MIN(cast_info.person_id), MIN(cast_info.note), MIN(title.kind_id), MIN(kind_type.id), MIN(title.title), MIN(name.id), MIN(title.id), MIN(title.production_year), MIN(movie_info.note), MIN(name.name_pcode_cf), MIN(company_type.id), MIN(movie_companies.company_type_id), MIN(cast_info.movie_id), MIN(company_type.kind), MIN(kind_type.kind) FROM name, title, movie_info, cast_info, movie_companies, company_type, kind_type, role_type WHERE movie_info.note = '(limited)' AND company_type.kind IN ('special effects companies', 'miscellaneous companies', 'distributors', 'production companies') AND name.name_pcode_cf != 'I326' AND role_type.role IS NULL AND title.title IS NULL AND cast_info.note = '(writer)' AND kind_type.kind != 'movie' AND title.production_year = 2000 AND company_type.id = movie_companies.company_type_id AND title.id = cast_info.movie_id AND kind_type.id = title.kind_id AND name.id = cast_info.person_id;