SELECT MIN(name.gender), MIN(title.id), MIN(movie_keyword.movie_id), MIN(movie_info.note), MIN(movie_info.movie_id) FROM name, title, movie_info, movie_keyword WHERE movie_info.note IN ('(f) (rating 1996)', '(limited)', '(studio)', '(Getty Center)', '(DVD premiere)', '(including commercials)', '(New York City, New York)', 'Stacy Teixeira', 'KGF Vissers', '(Finland: 2002)', '(featured on Unforgiven DVD)', 'Joseph Hollabaugh and HollabaughFilms', '(No. 41246)', '(1 reel)', '(certificate #37629)', 'Kevlar01', '(certificate #30342)', '(TV premiere)', 'Anonymous', '(video premiere)') AND name.gender != 'm' AND movie_keyword.movie_id = movie_info.movie_id AND movie_info.movie_id = title.id;