SELECT MIN(role_type.role), MIN(movie_info.info), MIN(cast_info.note), MIN(cast_info.person_id), MIN(comp_cast_type.kind), MIN(name.gender), MIN(title.title), MIN(aka_name.person_id), MIN(name.name), MIN(movie_companies.movie_id), MIN(movie_link.movie_id), MIN(complete_cast.status_id), MIN(comp_cast_type.id), MIN(aka_name.name), MIN(movie_info.movie_id) FROM name, movie_info, title, cast_info, aka_name, movie_companies, movie_link, role_type, complete_cast, comp_cast_type WHERE aka_name.name = 'Saldierna, Rechina' AND name.gender IS NULL AND title.title != 'Shipshape-Less' AND comp_cast_type.kind IN ('crew', 'complete+verified', 'complete', 'cast') AND movie_info.info != 'Although the film was made for just Â£160,000, producer-directors Gary Sinyor and Vadim Jean found it virtually impossible to raise the money for the production. It was only that the entire cast and crew worked for nothing on deferred payment salary that they were able to make the film.' AND cast_info.note IN ('(uncredited)', '(executive producer)', '(accountant: TFC Trickompany)', '(as Bulgari)', '(production assistant)', '(as Joe Pazos)', '(writer)', '(as Gustave Von Seyffertitz)', '(as Rosa Frausto)', '(producer) (as Koh Shibusawa)', '(script supervisor) (as Schno Mozingo)', '(co-executive producer) (as Tron Hauge)', '(voice)', '(production coordinator)', '(creator)', '(co-executive producer) (as Lyn Greene)', '(associate producer)', '(producer)', '(written by)', '(production financing) (as Christl Bucina)') AND role_type.role IN ('guest', 'producer', 'director', 'cinematographer', 'miscellaneous crew', 'actress', 'composer', 'production designer', 'actor', 'writer', 'costume designer', 'editor') AND name.name = 'Williams, Michael' AND movie_info.movie_id = movie_companies.movie_id AND cast_info.person_id = aka_name.person_id AND movie_link.movie_id = movie_info.movie_id AND comp_cast_type.id = complete_cast.status_id;