SELECT MIN(cast_info.note), MIN(title.episode_nr), MIN(info_type.info), MIN(movie_info.note), MIN(aka_name.name) FROM movie_info, title, aka_name, cast_info, info_type WHERE movie_info.note IS NOT NULL AND cast_info.note = '(as Gustave Von Seyffertitz)' AND aka_name.name = 'Strait, George Harvey' AND info_type.info != 'opening weekend' AND title.episode_nr IN (21755, 23508, 59295, 554, 6901, 85991, 78100);