SELECT MIN(movie_info.info), MIN(comp_cast_type.kind), MIN(movie_companies.note), MIN(kind_type.kind) FROM comp_cast_type, movie_info, movie_companies, kind_type WHERE movie_companies.note = '(1986) (USA) (VHS) (as "Beyond the Living Dead")' AND comp_cast_type.kind != 'crew' AND movie_info.info != 'The Doctor: It may be irrational of me, but human beings are quite my favorite species.' AND kind_type.kind != 'movie';