SELECT MIN(title.title), MIN(movie_companies.movie_id), MIN(title.id), MIN(complete_cast.movie_id), MIN(company_type.kind) FROM movie_companies, title, complete_cast, company_type WHERE title.title IN ('(#1.6)', '(#1.5)', 'El Señor Presidente', 'The Boardroom', 'A Visita da Velha Senhora', '(#1.1)', '(#1.10)', '51º edición de los premios Ondas', '(#1.8)', '(#1.7)', 'Song 25', '(#1.2)', '(#1.4)', 'Shipshape-Less', 'Zweedse rapsodie', '(#2.127)', '(#1.3)', '(#1.9)', 'Star Power', 'Eine Allerweltsgeschichte') AND company_type.kind = 'distributors' AND movie_companies.movie_id = title.id AND title.id = complete_cast.movie_id;