SELECT MIN(role_type.role), MIN(cast_info.person_id), MIN(name.gender), MIN(cast_info.role_id), MIN(name.id), MIN(company_name.country_code), MIN(company_name.id), MIN(role_type.id), MIN(movie_companies.company_id) FROM name, movie_companies, cast_info, role_type, company_name WHERE name.gender IN ('m', 'f', '') AND company_name.country_code IN ('[kr]', '[ve]', '[in]', '[fr]', '[it]', '[us]', '[ee]', '[gb]', '[th]', '[jp]', '[au]', '[es]', '[ki]', '[de]', '[ca]', '[bg]', '[na]', '[bm]', '[mn]', '[gl]') AND role_type.role = 'producer' AND company_name.id = movie_companies.company_id AND name.id = cast_info.person_id AND role_type.id = cast_info.role_id;