SELECT MIN(movie_info.info), MIN(movie_companies.note), MIN(person_info.person_id), MIN(title.episode_nr), MIN(aka_name.person_id), MIN(movie_companies.movie_id), MIN(title.id), MIN(movie_keyword.movie_id), MIN(movie_info_idx.movie_id), MIN(movie_link.movie_id), MIN(name.id), MIN(complete_cast.movie_id), MIN(movie_link.linked_movie_id), MIN(company_name.id), MIN(aka_name.name), MIN(company_type.kind), MIN(movie_companies.company_id), MIN(movie_info.movie_id) FROM movie_companies, title, aka_name, movie_info, name, company_type, movie_link, movie_keyword, movie_info_idx, complete_cast, person_info, company_name WHERE company_type.kind IN ('miscellaneous companies', 'special effects companies', 'production companies', 'distributors') AND movie_info.info = 'Kaori is pursuing her career as a journalist for a magazine with great enthusiasm. But as a result of an article she wrote, she is sent to work for a community magazine at Fukuoka. An anonymous letter arrives, which puts her in contact with an old and forgotten theater, the ''Minato Theater'' in nearby Shimonoseki. She also becomes acquainted with Shuhei Yasukawa, a popular performer of the early 60''s, who used to sing and play the guitar during interludes. This was an epoch when the cinema was at the height of its popularity. She decides to write an article on him. While covering, she discovers the ups and downs of his life. His daughter, Misato plays an important role for him, but between them exists a deep split.' AND movie_companies.note = '(1996) (USA) (VHS) (on "Bucky''s Dragon Art Theatre Triple XXX Double Feature Vol. 1")' AND title.episode_nr < 38742 AND aka_name.name != 'Medlin, Matt' AND person_info.person_id = aka_name.person_id AND movie_link.linked_movie_id = movie_companies.movie_id AND movie_link.movie_id = movie_companies.movie_id AND title.id = movie_link.movie_id AND movie_info.movie_id = title.id AND movie_info.movie_id = complete_cast.movie_id AND name.id = aka_name.person_id AND movie_info_idx.movie_id = title.id AND movie_companies.movie_id = movie_keyword.movie_id AND title.id = movie_keyword.movie_id AND company_name.id = movie_companies.company_id AND movie_link.movie_id = movie_info.movie_id AND movie_keyword.movie_id = movie_info.movie_id;