SELECT MIN(role_type.role), MIN(movie_info_idx.info), MIN(link_type.link), MIN(movie_info.note), MIN(movie_link.movie_id), MIN(movie_info.movie_id) FROM link_type, movie_info, movie_link, movie_info_idx, role_type WHERE movie_info.note != '(including commercials)' AND link_type.link IS NULL AND movie_info_idx.info = '6.6' AND role_type.role IN ('editor', 'writer', 'composer', 'cinematographer', 'miscellaneous crew', 'director', 'production designer', 'actress', 'costume designer', 'actor', 'producer', 'guest') AND movie_link.movie_id = movie_info.movie_id;