SELECT MIN(movie_info.info), MIN(cast_info.note), MIN(company_name.name), MIN(name.gender), MIN(title.title), MIN(title.episode_nr), MIN(name.name), MIN(title.id), MIN(link_type.link), MIN(aka_name.name), MIN(cast_info.movie_id), MIN(movie_info.movie_id) FROM movie_info, name, title, cast_info, link_type, aka_name, company_name WHERE name.name != 'Smith, Steve' AND aka_name.name = 'Callaghan, Leonard James' AND link_type.link IN ('spin off from', 'referenced in', 'featured in', 'followed by', 'edited from', 'spoofs', 'remade as', 'remake of', 'spin off', 'features', 'edited into', 'version of', 'references', 'unknown link', 'similar to', 'alternate language version of', 'spoofed in', 'follows') AND movie_info.info = 'The hovercraft chase sequence was filmed nearby to a working airport. Pilots are understandably nervous about seeing gunfire and explosions at an airport so a schedule had to be worked out whereby filming could take place whenever the airport wasn''t too busy.' AND title.title != '(#1.5)' AND cast_info.note != '(creator)' AND title.episode_nr BETWEEN 40920 AND 48294 AND name.gender IS NULL AND company_name.name IN ('Overnight Productions', 'Westofi-Tonfilm-Productions', 'Universal Pictures International (UPI)', 'Flying Rhino Productions', 'Sony Pictures Releasing', 'Atmospheric Pictures', 'AXN', 'Nickelodeon', 'Buena Vista International', 'Walt Disney Studios Motion Pictures', 'Reel DVD', '20th Century Fox', 'United International Pictures (UIP)', 'Trends Media Group', 'DOD Entertainment', 'H.I.S.', 'No Fear Here Productions', 'Sony Entertainment Television', 'Warner Bros.', 'Bae Yong-Kyun Productions') AND movie_info.movie_id = cast_info.movie_id AND title.id = cast_info.movie_id;