SELECT MIN(role_type.role), MIN(info_type.id), MIN(comp_cast_type.kind), MIN(name.gender), MIN(person_info.info_type_id), MIN(info_type.info), MIN(complete_cast.status_id), MIN(name.name_pcode_cf), MIN(comp_cast_type.id), MIN(company_name.country_code) FROM name, info_type, role_type, complete_cast, person_info, company_name, comp_cast_type WHERE role_type.role = 'composer' AND name.gender IS NOT NULL AND company_name.country_code IN ('[de]', '[bg]', '[fr]', '[us]', '[es]', '[mn]', '[bm]', '[th]', '[ki]', '[kr]', '[ee]', '[jp]', '[gl]', '[in]', '[na]', '[it]', '[gb]', '[au]', '[ve]', '[ca]') AND info_type.info != 'LD original title' AND comp_cast_type.kind IN ('complete', 'crew', 'complete+verified', 'cast') AND name.name_pcode_cf != 'R1632' AND comp_cast_type.id = complete_cast.status_id AND info_type.id = person_info.info_type_id;