SELECT MIN(role_type.role), MIN(movie_info.movie_id), MIN(company_type.kind), MIN(movie_keyword.movie_id), MIN(movie_info.note), MIN(company_name.country_code), MIN(kind_type.kind) FROM movie_info, company_type, kind_type, movie_keyword, role_type, company_name WHERE kind_type.kind IN ('tv movie', 'tv series', 'video movie', 'episode', 'tv mini series', 'movie', 'video game') AND company_type.kind != 'miscellaneous companies' AND role_type.role != 'editor' AND movie_info.note IN ('(video premiere)', '(No. 41246)', '(Getty Center)', 'Stacy Teixeira', '(including commercials)', '(studio)', 'KGF Vissers', '(limited)', '(certificate #30342)', '(featured on Unforgiven DVD)', '(1 reel)', 'Kevlar01', '(DVD premiere)', 'Anonymous', '(Finland: 2002)', '(New York City, New York)', '(f) (rating 1996)', '(TV premiere)', '(certificate #37629)', 'Joseph Hollabaugh and HollabaughFilms') AND company_name.country_code != '[in]' AND movie_keyword.movie_id = movie_info.movie_id;