SELECT MIN(movie_info_idx.info), MIN(title.id), MIN(movie_info.movie_id), MIN(movie_link.movie_id), MIN(aka_title.movie_id), MIN(cast_info.person_id), MIN(movie_info.info), MIN(char_name.name), MIN(title.title), MIN(name.id), MIN(company_name.name), MIN(name.gender) FROM title, name, movie_info_idx, char_name, movie_link, aka_title, company_name, movie_info, cast_info WHERE movie_info.info = 'Short' AND title.title IS NOT NULL AND company_name.name IS NULL AND name.gender != '' AND movie_info_idx.info != '01..0003.2' AND char_name.name = 'Madame de Neiss' AND title.id = movie_link.movie_id AND movie_info.movie_id = aka_title.movie_id AND name.id = cast_info.person_id;