SELECT MIN(title.id), MIN(movie_companies.company_type_id), MIN(aka_title.movie_id), MIN(movie_companies.movie_id), MIN(company_type.id), MIN(movie_companies.note) FROM title, movie_companies, company_type, aka_title WHERE movie_companies.note = '(1997) (Japan) (all media) (Pippin Atmark)' AND movie_companies.movie_id = aka_title.movie_id AND movie_companies.movie_id = title.id AND company_type.id = movie_companies.company_type_id;