SELECT MIN(person_info.note), MIN(cast_info.person_id), MIN(aka_name.person_id), MIN(name.id), MIN(name.name_pcode_cf), MIN(person_info.person_id) FROM cast_info, name, person_info, aka_name WHERE name.name_pcode_cf = 'M6352' AND person_info.note IS NULL AND name.id = person_info.person_id AND name.id = cast_info.person_id AND name.id = aka_name.person_id AND cast_info.person_id = person_info.person_id;