SELECT MIN(link_type.link), MIN(movie_info.movie_id), MIN(aka_title.movie_id), MIN(movie_info.note), MIN(movie_keyword.movie_id), MIN(aka_name.name), MIN(aka_name.person_id), MIN(name.id), MIN(person_info.person_id) FROM movie_keyword, name, aka_name, link_type, person_info, aka_title, movie_info WHERE link_type.link = 'remake of' AND aka_name.name != 'Bob' AND movie_info.note != 'Stacy Teixeira' AND movie_info.movie_id = aka_title.movie_id AND movie_keyword.movie_id = movie_info.movie_id AND person_info.person_id = aka_name.person_id AND name.id = aka_name.person_id;