SELECT MIN(title.id), MIN(movie_info.movie_id), MIN(aka_title.movie_id), MIN(movie_keyword.movie_id), MIN(movie_info.info), MIN(cast_info.movie_id), MIN(movie_info_idx.movie_id) FROM title, movie_keyword, movie_info_idx, aka_title, movie_info, cast_info WHERE movie_info.info != 'Amanda Price: Elizabeth Bennett is lending me her mobile...' AND movie_info.movie_id = title.id AND movie_info.movie_id = aka_title.movie_id AND movie_info.movie_id = movie_info_idx.movie_id AND movie_keyword.movie_id = movie_info.movie_id AND movie_info.movie_id = cast_info.movie_id;