SELECT MIN(title.id), MIN(movie_link.movie_id), MIN(comp_cast_type.kind), MIN(aka_title.movie_id), MIN(movie_companies.movie_id), MIN(title.production_year), MIN(complete_cast.movie_id), MIN(cast_info.movie_id), MIN(movie_link.linked_movie_id) FROM title, comp_cast_type, complete_cast, movie_companies, aka_title, movie_link, cast_info WHERE title.production_year BETWEEN 1961 AND 1980 AND comp_cast_type.kind != 'complete' AND title.id = movie_link.movie_id AND title.id = movie_link.linked_movie_id AND title.id = aka_title.movie_id AND title.id = complete_cast.movie_id AND title.id = cast_info.movie_id AND movie_companies.movie_id = title.id;