SELECT MIN(movie_info.movie_id), MIN(movie_link.movie_id), MIN(aka_title.movie_id), MIN(movie_info.note), MIN(complete_cast.movie_id), MIN(cast_info.movie_id) FROM complete_cast, movie_link, aka_title, movie_info, cast_info WHERE movie_info.note IS NOT NULL AND movie_info.movie_id = aka_title.movie_id AND movie_link.movie_id = movie_info.movie_id AND movie_info.movie_id = cast_info.movie_id AND movie_info.movie_id = complete_cast.movie_id;