SELECT MIN(link_type.link), MIN(company_type.kind), MIN(person_info.note), MIN(comp_cast_type.kind), MIN(keyword.id), MIN(movie_keyword.keyword_id), MIN(name.name), MIN(person_info.person_id), MIN(aka_name.name), MIN(aka_name.person_id), MIN(name.id), MIN(name.name_pcode_cf), MIN(kind_type.kind), MIN(company_name.name), MIN(keyword.keyword) FROM keyword, movie_keyword, name, kind_type, comp_cast_type, link_type, person_info, company_name, aka_name, company_type WHERE link_type.link IN ('references', 'remake of', 'alternate language version of', 'spoofed in', 'unknown link', 'featured in', 'spin off', 'edited from', 'similar to', 'features', 'spoofs', 'edited into', 'version of', 'spin off from', 'followed by', 'remade as', 'referenced in', 'follows') AND person_info.note = 'lkp' AND aka_name.name = 'Medlin, Matt' AND company_type.kind IS NULL AND comp_cast_type.kind IN ('cast', 'complete+verified', 'crew', 'complete') AND name.name_pcode_cf IS NOT NULL AND name.name = 'Smith, Michael' AND kind_type.kind = 'tv mini series' AND keyword.keyword IS NOT NULL AND company_name.name = 'No Fear Here Productions' AND person_info.person_id = aka_name.person_id AND name.id = person_info.person_id AND keyword.id = movie_keyword.keyword_id;