SELECT MIN(cast_info.person_id), MIN(movie_companies.company_id), MIN(name.name), MIN(aka_name.person_id), MIN(name.id), MIN(company_name.id), MIN(company_name.country_code) FROM name, movie_companies, company_name, aka_name, cast_info WHERE company_name.country_code IS NULL AND name.name = 'Henry, Cory' AND company_name.id = movie_companies.company_id AND name.id = cast_info.person_id AND name.id = aka_name.person_id;