SELECT MIN(company_type.kind), MIN(title.kind_id), MIN(kind_type.id), MIN(title.production_year), MIN(title.title), MIN(title.episode_nr), MIN(company_name.name) FROM title, company_type, kind_type, company_name WHERE company_name.name = 'Flying Rhino Productions' AND title.episode_nr > 62374 AND title.production_year NOT BETWEEN 1922 AND 1946 AND title.title != '(#1.2)' AND company_type.kind = 'miscellaneous companies' AND kind_type.id = title.kind_id;