SELECT MIN(role_type.role), MIN(link_type.link), MIN(company_type.kind), MIN(comp_cast_type.kind), MIN(movie_companies.company_type_id), MIN(name.name), MIN(company_type.id), MIN(kind_type.kind), MIN(title.episode_nr), MIN(movie_info_idx.info) FROM title, role_type, name, kind_type, comp_cast_type, movie_info_idx, link_type, movie_companies, company_type WHERE company_type.kind IN ('production companies', 'special effects companies', 'distributors', 'miscellaneous companies') AND kind_type.kind IS NULL AND movie_info_idx.info = '7' AND role_type.role IN ('editor', 'miscellaneous crew', 'costume designer', 'director', 'actress', 'actor', 'composer', 'cinematographer', 'guest', 'producer', 'production designer', 'writer') AND name.name != 'Jones, David' AND title.episode_nr != 41868 AND comp_cast_type.kind IS NULL AND link_type.link = 'spin off from' AND company_type.id = movie_companies.company_type_id;