SELECT MIN(role_type.role), MIN(title.kind_id), MIN(movie_link.movie_id), MIN(movie_info.movie_id), MIN(kind_type.id), MIN(movie_info.note), MIN(kind_type.kind) FROM title, role_type, kind_type, movie_link, movie_info WHERE movie_info.note = '(video premiere)' AND kind_type.kind IS NULL AND role_type.role IS NOT NULL AND movie_link.movie_id = movie_info.movie_id AND kind_type.id = title.kind_id;