SELECT MIN(role_type.role), MIN(title.id), MIN(movie_info.note), MIN(char_name.name), MIN(cast_info.note), MIN(cast_info.movie_id), MIN(company_name.name) FROM title, role_type, char_name, company_name, movie_info, cast_info WHERE role_type.role = 'costume designer' AND movie_info.note = '(including commercials)' AND char_name.name IN ('Übersetzungsvoiceover', 'Angiolino', 'Himself - Host', 'Archibald', 'Narrator', 'Himself', 'Himself -', 'Madame de Neiss', 'Jan Tobek', 'Cpt. Sokolowski', 'Maze', 'Herself', 'Himself - Field Reporter', 'Docville Bank Teller', 'Taiga Samejima', 'Claire Killearn', 'Édouard Cordier', 'Kiki Small', 'Herself -', 'Corey Feldman''s Body Double') AND cast_info.note IS NULL AND company_name.name IS NULL AND title.id = cast_info.movie_id;