SELECT MIN(role_type.role), MIN(title.id), MIN(role_type.id), MIN(cast_info.role_id), MIN(aka_name.name), MIN(title.title), MIN(kind_type.kind), MIN(cast_info.movie_id), MIN(movie_link.linked_movie_id), MIN(movie_info_idx.movie_id) FROM title, role_type, kind_type, movie_info_idx, movie_link, aka_name, cast_info WHERE aka_name.name IS NULL AND role_type.role != 'producer' AND kind_type.kind = 'tv series' AND title.title IS NOT NULL AND title.id = cast_info.movie_id AND role_type.id = cast_info.role_id AND title.id = movie_link.linked_movie_id AND movie_info_idx.movie_id = title.id;