SELECT MIN(role_type.role), MIN(link_type.link), MIN(title.id), MIN(company_type.kind), MIN(movie_link.movie_id), MIN(movie_companies.company_type_id), MIN(movie_companies.movie_id), MIN(company_type.id), MIN(title.production_year), MIN(complete_cast.movie_id) FROM title, role_type, complete_cast, link_type, movie_companies, movie_link, company_type WHERE title.production_year < 1892 AND company_type.kind IN ('distributors', 'production companies', 'special effects companies', 'miscellaneous companies') AND link_type.link IS NOT NULL AND role_type.role IN ('costume designer', 'actor', 'writer', 'composer', 'cinematographer', 'editor', 'producer', 'guest', 'production designer', 'miscellaneous crew', 'director', 'actress') AND movie_companies.movie_id = title.id AND title.id = complete_cast.movie_id AND title.id = movie_link.movie_id AND company_type.id = movie_companies.company_type_id;