SELECT MIN(link_type.link), MIN(person_info.note), MIN(movie_keyword.movie_id), MIN(keyword.keyword), MIN(movie_info_idx.movie_id), MIN(movie_info_idx.info) FROM keyword, movie_keyword, movie_info_idx, link_type, person_info WHERE movie_info_idx.info IS NOT NULL AND link_type.link != 'unknown link' AND person_info.note = 'anonymous' AND keyword.keyword IS NOT NULL AND movie_info_idx.movie_id = movie_keyword.movie_id;