SELECT MIN(role_type.role), MIN(link_type.link), MIN(cast_info.person_id), MIN(aka_name.person_id), MIN(char_name.name), MIN(cast_info.note), MIN(cast_info.movie_id), MIN(movie_info_idx.movie_id) FROM role_type, movie_info_idx, char_name, link_type, aka_name, cast_info WHERE role_type.role IS NOT NULL AND cast_info.note != '(uncredited)' AND link_type.link != 'spoofs' AND char_name.name IN ('Himself', 'Herself', 'Jan Tobek', 'Claire Killearn', 'Himself - Host', 'Narrator', 'Docville Bank Teller', 'Himself - Field Reporter', 'Kiki Small', 'Himself -', 'Angiolino', 'Übersetzungsvoiceover', 'Taiga Samejima', 'Cpt. Sokolowski', 'Corey Feldman''s Body Double', 'Madame de Neiss', 'Archibald', 'Herself -', 'Édouard Cordier', 'Maze') AND cast_info.movie_id = movie_info_idx.movie_id AND cast_info.person_id = aka_name.person_id;