SELECT MIN(link_type.link), MIN(movie_info.note), MIN(kind_type.kind), MIN(movie_companies.note), MIN(company_name.name) FROM kind_type, link_type, movie_companies, company_name, movie_info WHERE link_type.link IN ('follows', 'unknown link', 'spin off', 'references', 'alternate language version of', 'spin off from', 'followed by', 'similar to', 'referenced in', 'remade as', 'edited into', 'edited from', 'remake of', 'features', 'version of', 'spoofs', 'featured in', 'spoofed in') AND company_name.name != 'DOD Entertainment' AND movie_info.note IN ('Stacy Teixeira', '(including commercials)', '(No. 41246)', '(featured on Unforgiven DVD)', '(certificate #37629)', 'Kevlar01', '(Finland: 2002)', 'KGF Vissers', '(Getty Center)', '(DVD premiere)', 'Anonymous', '(studio)', '(video premiere)', '(certificate #30342)', '(1 reel)', '(limited)', '(TV premiere)', '(f) (rating 1996)', '(New York City, New York)', 'Joseph Hollabaugh and HollabaughFilms') AND movie_companies.note = '(2009) (USA) (DVD)' AND kind_type.kind IN ('movie', 'tv series', 'tv movie', 'video game', 'video movie', 'episode', 'tv mini series');