SELECT MIN(role_type.role), MIN(movie_info.info_type_id), MIN(movie_info_idx.info_type_id), MIN(movie_companies.company_type_id), MIN(movie_companies.company_id), MIN(name.id), MIN(movie_companies.movie_id), MIN(kind_type.kind), MIN(person_info.person_id), MIN(info_type.id), MIN(char_name.name), MIN(company_type.id), MIN(company_name.id), MIN(name.name_pcode_cf), MIN(info_type.info), MIN(movie_companies.note), MIN(movie_info_idx.movie_id) FROM role_type, name, kind_type, movie_info_idx, char_name, movie_companies, person_info, company_name, info_type, movie_info, company_type WHERE movie_companies.note = '(presents)' AND role_type.role IN ('writer', 'actor', 'editor', 'composer', 'producer', 'costume designer', 'guest', 'actress', 'director', 'production designer', 'cinematographer', 'miscellaneous crew') AND char_name.name = 'Himself - Field Reporter' AND name.name_pcode_cf IS NULL AND kind_type.kind IN ('tv series', 'tv movie', 'video movie', 'episode', 'video game', 'tv mini series', 'movie') AND info_type.info IS NOT NULL AND company_type.id = movie_companies.company_type_id AND info_type.id = movie_info.info_type_id AND name.id = person_info.person_id AND movie_info_idx.movie_id = movie_companies.movie_id AND info_type.id = movie_info_idx.info_type_id AND company_name.id = movie_companies.company_id;