SELECT MIN(role_type.role), MIN(title.id), MIN(cast_info.movie_id), MIN(movie_info.movie_id), MIN(name.name), MIN(movie_companies.movie_id), MIN(movie_info.info), MIN(name.name_pcode_cf), MIN(movie_companies.note), MIN(name.gender) FROM title, role_type, name, movie_companies, movie_info, cast_info WHERE movie_companies.note = '(2008) (USA) (DVD)' AND role_type.role != 'actress' AND name.name != 'Morgan, Alexandra' AND movie_info.info != 'Frank: Oi! Izzard! No!' AND name.name_pcode_cf = 'R1632' AND name.gender IN ('m', '', 'f') AND movie_info.movie_id = title.id AND movie_companies.movie_id = cast_info.movie_id;