SELECT MIN(link_type.link), MIN(movie_info.movie_id), MIN(person_info.note), MIN(aka_title.movie_id), MIN(cast_info.person_id), MIN(name.name), MIN(movie_info.note), MIN(movie_companies.movie_id), MIN(info_type.id), MIN(char_name.name), MIN(title.title), MIN(name.id), MIN(person_info.person_id), MIN(movie_companies.note), MIN(movie_link.linked_movie_id), MIN(person_info.info_type_id), MIN(keyword.keyword) FROM keyword, title, name, link_type, char_name, movie_companies, movie_link, person_info, aka_title, info_type, movie_info, cast_info WHERE person_info.note != 'Hup234!' AND title.title IS NOT NULL AND keyword.keyword IN ('haitian-voodoo', 'designer-handbag', 'communications-director', 'lake-havasu', 'barrier-cliff', 'munitions-train', 'bunny-love', 'reference-to-hugh-o''neill-earl-of-tyrone', 'butchering-cattle', 'socialismus', 'congressional-gold-medal', 'unhappiness', 'actuary', 'covered-in-mud', 'coal-town', 'baseball-game', 'religious-sect', 'suicide-by-falling', 'electronic-bank-robbery', 'naval-escort') AND movie_companies.note = '(2007) (USA) (DVD)' AND movie_info.note IS NULL AND name.name != 'Smith, Michael' AND char_name.name != 'Jan Tobek' AND link_type.link IN ('references', 'follows', 'similar to', 'spoofed in', 'unknown link', 'followed by', 'alternate language version of', 'edited into', 'referenced in', 'edited from', 'spin off', 'features', 'spoofs', 'featured in', 'remade as', 'spin off from', 'version of', 'remake of') AND info_type.id = person_info.info_type_id AND cast_info.person_id = person_info.person_id AND movie_link.linked_movie_id = movie_companies.movie_id AND name.id = person_info.person_id AND movie_info.movie_id = aka_title.movie_id;