SELECT MIN(title.id), MIN(company_type.kind), MIN(person_info.note), MIN(name.name), MIN(title.episode_nr), MIN(complete_cast.movie_id), MIN(cast_info.note) FROM title, name, cast_info, complete_cast, person_info, company_type WHERE company_type.kind = 'production companies' AND name.name = 'Stalzer, Chris' AND person_info.note IS NOT NULL AND cast_info.note IS NOT NULL AND title.episode_nr > 12727 AND title.id = complete_cast.movie_id;