SELECT MIN(link_type.link), MIN(movie_info_idx.info_type_id), MIN(info_type.id), MIN(title.title), MIN(name.name_pcode_cf), MIN(info_type.info), MIN(keyword.keyword), MIN(name.gender) FROM keyword, title, name, movie_info_idx, link_type, info_type WHERE name.name_pcode_cf = 'R1632' AND name.gender = '' AND keyword.keyword != 'baseball-game' AND link_type.link = 'spin off' AND title.title = '(#1.4)' AND info_type.info = 'votes distribution' AND info_type.id = movie_info_idx.info_type_id;